(defn day-one-part-one []
  (let [lefts (count (re-seq #"\(" input)) 
        rights (count (re-seq #"\)" input))]
        (- lefts rights)))

(defn day-one-part-two []
  (loop [elevator input
         i 0
         floor 0]
    (if (< floor 0) 
      i
      (recur 
        (rest elevator)
        (inc i)
        (if (= "(" (str (first elevator)))
          (inc floor)
          (dec floor))))))
